import * as React from "react";
import './Decor.css'
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { Button } from "@mui/material";
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

export const Form = (props) => {

  const [timeZone, setTimeZone] = React.useState("");  
  function handleChange(event)
  {
    setTimeZone(event.target.value)
  }
  let ind = 0;
  const listElements = props.myArr.map(item => {
    ind++;
    if(ind === 1)
    {
      return <MenuItem value={item}></MenuItem>
    }
    else
    {
      return <MenuItem value={item}></MenuItem>
    }
  })
  return (
    <div className="page--form">
      <h3>Select Timezone: </h3>
      <div className="page--div">
      <FormControl className="page--dropdown">
        <InputLabel id="demo-simple-select-label">Pick</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={(props.myArr)[0]}
          label="Age"
          onChange={handleChange}
        >

          {listElements}

        </Select>
      </FormControl>
      <Button><ArrowForwardIcon /></Button>
      </div>
    </div>
  );
};
