import * as React from "react";
import './Decor.css'
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { Button } from "@mui/material";
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

export const Form = () => {

  const [timeZone, setTimeZone] = React.useState("");  
  function handleChange(event)
  {
    setTimeZone(event.target.value)
  }

  const URL = "http://worldtimeapi.org/api/timezone/";
    var requestOptions = {
        method: 'GET',
        redirect: 'follow'
    };


    const [myArr, setMyArr] = useState([]);
    const [reload, setReload] = useState(true);

    async function makeApiCall(url) {
        let response = await fetch(url, requestOptions);
        response = await response.json();
        let newArr = await Array.from(response);
        // console.log(newArr)
        let mySet = new Set();
        //console.log(mySet.size)
        while (mySet.size < 6) {
            let rand = Math.floor(Math.random() * newArr.length);
            mySet.add(newArr[rand]);
        }
        console.log(Array.from(mySet));
        setMyArr(Array.from(mySet));
    }


    useEffect(()=>{
        if (myArr.length === 0 && reload === true) {
            setReload(false);
            makeApiCall(URL);
        }
    })



  return (
    <div className="page--form">
      <h3>Select Timezone: </h3>
      <div className="page--div">
      <FormControl className="page--dropdown">
        <InputLabel id="demo-simple-select-label">Pick</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={timeZone}
          label="Age"
          onChange={handleChange}
        >
          <MenuItem value={""}>--Choose your Timezone--</MenuItem>
          <MenuItem value={"10"}>Ten</MenuItem>
          <MenuItem value={"20"}>Twenty</MenuItem>
          <MenuItem value={"30"}>Thirty</MenuItem>
        </Select>
      </FormControl>
      <Button><ArrowForwardIcon /></Button>
      </div>
    </div>
  );
};
