import * as React from "react";
import "./Decor.css";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import { Button } from "@mui/material";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";

export const Form = (props) => {
  const [timeZone, setTimeZone] = React.useState("");
  console.log(props.myArr);
  // function handleChange(event) {
  //   setTimeZone(event.target.value);
  // }

  let listElements;
  React.useEffect(() => {
    if (props.myArr.length !== 0) {
      setTimeZone(props.myArr[0]);

      let ind = 0;
      listElements = props.myArr.map((item) => {
        console.log(item);
        ind++;
        return <MenuItem value={item} key={ind}></MenuItem>;
      });
    }
  }, [props.myArr]);

  return (
    <div className="page--form">
      <h3>Select Timezone: </h3>
      <div className="page--div">
        <FormControl className="page--dropdown">
          <InputLabel id="demo-simple-select-label">Pick</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={timeZone}
            label="Time Zone"
            // onChange={handleChange}
          >
            {props.myArr.length !== 0 && listElements}
          </Select>
        </FormControl>
        <Button>
          <ArrowForwardIcon />
        </Button>
      </div>
    </div>
  );
};
