import * as React from "react";
import "./Decor.css";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import { Button } from "@mui/material";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";

export const Form = (props) => {
  const [timeZone, setTimeZone] = React.useState(props.myArr[0]);
  function handleChange(event) {
    setTimeZone(event.target.value);
  }

  const listElemets = props.myArr.map((item) => {
    return <MenuItem value={item}>{item}</MenuItem>;
  });

  return (
    <div className="page--form">
      <h3>Select Timezone: </h3>
      <div className="page--div">
        <FormControl className="page--dropdown">
          <InputLabel id="demo-simple-select-label">Pick</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={timeZone}
            label="Age"
            onChange={handleChange}
          >
            {props.myArr.length !== 0 && listElemets}
          </Select>
        </FormControl>
        <Button>
          <ArrowForwardIcon />
        </Button>
      </div>
    </div>
  );
};
