import React, { useState, useEffect } from 'react'
import { MyCard } from './MyCard'
import axios from 'axios'

export const Home = () => {

    // 
    // var requestOptions = {
    //     method: 'GET',
    //     redirect: 'follow'
    // };



    // const [reload, setReload] = useState(true);

    // async function makeApiCall(url) {
    //     let response = await fetch(url, requestOptions);

    // }



    // if (myArr.length === 0 && reload === true) {
    //     setReload(false);
    //     makeApiCall(URL);
    // }

    const URL = "http://worldtimeapi.org/api/timezone/";
    const [myArr, setMyArr] = useState([]);

    useEffect(() => {
        const getItems = async () => {
            const result = await axios(
                url
            )

            let response = await result.data;
            let newArr = await Array.from(response);
            // console.log(newArr)
            let mySet = new Set();
            //console.log(mySet.size)
            while (mySet.size < 6) {
                let rand = Math.floor(Math.random() * newArr.length);
                mySet.add(newArr[rand]);
            }
            console.log(Array.from(mySet));
            setMyArr(Array.from(mySet));
        }
        getItems()

    }, [])

    let cardElemets;
    function getItems() {
        let ind = 0;
        cardElemets = myArr.map(item => {
            ind++;
            return <MyCard item={item} id={ind} key={ind} />
        })
    }



    return (
        <div className='grid--container'>
            {myArr.length !== 0 && cardElemets}
        </div>
    )
}
